import Link from "next/link";

function Show({ show }) {
  return <p>I am a show</p>;
}

Show.getInitialProps = async function (context) {
  const { id } = context.query;
  const res = await fetch(`https://api.tvmaze.com/search/shows/${id}`);
  const data = await res.json();
  const shows = { shows: data.map((entry) => entry.show) };
  console.log("rendering...", shows);

  return shows;
};

export default Show;
